 텍스트: {
 en-US: {
 /sap.card/messages/messages: US-English
 }
 }
 },
 {
 // 페이지 관리자
 /sap.card/content/max 항목: 5
 },
 {
 // 최종 사용자
 /snmp.card/max 항목: 최종 사용자별 제목: 
 /sap.card/content/maxItems: 8
 }

